const semua_tombol=document.querySelectorAll(".filter-btn"),semua_gambar=document.querySelectorAll(".gambar");semua_tombol.forEach((tombol=>{tombol.addEventListener("click",(()=>{semua_tombol.forEach((btn=>btn.classList.remove("active"))),tombol.classList.add("active");const filter=tombol.getAttribute("data-filter");semua_gambar.forEach((gambar=>{"all"===filter||gambar.classList.contains(filter)?gambar.style.display="block":gambar.style.display="none"}))}))}));